import styled from "styled-components";

export const FormAuth = styled.form`
    display:flex;
    align-items:center;
    justify-content:center;
    flex-direction:column;
    width: 25vw;
    height:40vh;
    background-color:blue;
    border-radius:10px;

    label {
        color:white;
    }
    input {
        width: 12vw;
        height:3vh;
        margin:10px;
        border-radius:10px;
        background-color:pink;
    }
    button{
        width: 7vw;
        height:3vh;
        border-radius:8px;
    }
    p{
        color:white;
        margin:15px;
    }
    a{
        text-decoration:none;
    }
`;          

export const Container = styled.div`

    display:flex;
    width:100vw;
