import api from "../../utils/api";

import {Container, Title, ContainerProduto} from "./StylesHome"

import {useState, useEffect } from "react";
import { Link } from "react-router-dom";

function Home() {
    const [produtos, setProdutos] = useState([])
    useEffect(() => {
        api.get('/produtos/ProdutosCadastrados').then((response) => {
          setProdutos(response.data)
        })
      }, [])

    return (  
        <>
        <Title>
          <h1>Meus Produtos</h1>
          <Link to= '/produtos/cadastrar'>Cadastrar produto</Link>
        </Title>
        <Container>
            <div>
                <h1>Compre um produto</h1>
                <p>Veja os detalhes de cada um</p>
            </div>
            <div> 
                {produtos.length > 0 &&
                produtos.map((produto) => (
                    <ContainerProduto key={produto._id}>
                        <div style={{
                            backgroundImage: `url(${process.env.REACT_APP_API}/images/produtos/${produto.imagens[0]})`,
                            }}
                        ></div>
                        <h3>{produto.nome}</h3>
                        <p>
                            <span className="bold">Valor:</span> {produto.valor}
                        </p>
                        {produto.available ? (
                            <Link to={`/produto/${produto._id}`}>Mais detalhes</Link>
                        ) : (
                            <p>Comprado!</p>
                        )}
                    </ContainerProduto>
                ))}
                {produtos.length === 0 && (
                <p>Não há produtos cadastrados ou disponíveis para compra!</p>
                )}
            </div>
        </Container>

        </>
    );
}

export default Home;
