import React from 'react'
import { useState, useEffect } from 'react'
import {Link} from 'react-router-dom'

// utils
import api from '../../../utils/api'

// Hooks
import useFlashMessage from '../../../hooks/useFlashMessage'

function MyProdutos() {
    const [produtos, setProdutos] = useState([])
    const [token] = useState(localStorage.getItem('token'))

    // Usar as mensagens
    const {setFlashMessage} = useFlashMessage()

    useEffect(() => {
      api.get('/produtos/MeusProdutosCadastrados', {
        // Mandar o token para o headers
        headers: {
          Authorization: `Bearer ${JSON.parse(token)}`
          }
      }).then((response) => {
        setProdutos(response.data.produto)
      })
    }, [token])


  return (
    <section>
        <h1>Meus Produtos</h1>
        <Link to= '/produtos/cadastrar'>Cadastrar produto</Link>
        <div>
            {produtos.length > 0 &&
              produtos.map((produto) => {
                <div key={produto_id}>
                  <p>{produto.name}</p>
                </div>
              })
            }
            {produtos.length === 0 && <p>Não há produtos cadastrados</p>}
        </div>
    </section>

  )
}

export default MyProdutos
