import api from "../../utils/api";

import {useState, useEffect } from "react";

function Home() {
    const [produtos, setProdutos] = useState([])
    useEffect(() => {
        api.get('/produtos/ProdutosCadastrados').then((response) => {
          setProdutos(response.data)
        })
      }, [])

    return (  
        <section>
            <div>
                <h1>Compre um produto</h1>
                <p>Veja os detalhes de cada um</p>
            </div>
            <div>
                {produtos.length > 0 &&
                produtos.map((produto) => (
                    <div className={styles.pet_card} key={produto._id}>
                    <div
                        style={{
                        backgroundImage: `url(${process.env.REACT_APP_API}/images/produtos/${produto.images[0]})`,
                        }}
                        className={styles.pet_card_image}
                    ></div>
                    <h3>{produto.name}</h3>
                    <p>
                        <span className="bold">Peso:</span> {produto.weight}kg
                    </p>
                    {produto.available ? (
                        <Link to={`/produto/${produto._id}`}>Mais detalhes</Link>
                    ) : (
                        <p className={styles.adopted_text}>Adotado!</p>
                    )}
                    </div>
                ))}
                {produtos.length === 0 && (
                <p>Não há produtos cadastrados ou disponíveis para adoção no momento!</p>
                )}
            </div>
        </section>
    );
}

export default Home;
