import styled from "styled-components";


export const Container = styled.div`

    display:flex;
    width:100vw;
    height:80vh;
    background-color:white;
    padding:30px;

`;

export const Title = styled.div`
    background-color:green;
    display:flex;
    align-items:center;
    justify-content:center;
    flex-direction:column;
    height:10vh;
    width:100vw;

    h1{
        color:white;
    }
`;

export const ContainerProduto = styled.div`
    background-color:pink;
    width:20vw;
    height:45vh;
    margin:15px;
    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:center;

    img{
        width:18vw;
        height:300px;
        border-radius:40px;
    }

    p{
        color: black;
        margin:5px 0 5px 0;
        text-align:left;
    }
    button{
        width:120px;
        height:30px;
        border-radius:10px;
        margin-left:20px;
        border-width:0;
    }

    a{
        text-decoration:none;
        color:white;
    }
`;
