import api from '../../../utils/api';
//import styles from './Profile.module.css'
import Input from '../../form/Input'
import {useState, useEffect } from "react";
import useFlashMessage from '../../../hooks/useFlashMessage'

// Importe a classe FormData
import FormData from 'form-data'

function Profile() {
    const [usuario, setUsuario] = useState({})
    const {setFlashMessage} = useFlashMessage()

    // Pegar o token
    const [token] = useState(localStorage.getItem('token')|| "")

    useEffect(() => {

        // Pegar usuários pelo token
        api.get('/usuarios/checarUsuario',{
        headers:{
            Authorization: `Bearer ${JSON.parse(token)}`
        }
    }).then(response => {
        setUsuario(response.data.currentUser)
    })

    },[token])

    // Pegar os valores dos inputs
    function handleChange(e){
        setUsuario({...usuario, [e.target.name]: e.target.value})
    }

    // Função para editar o usuário
    async function handleSubmit(e){
        e.preventDefault()
        let msgType = 'success'

        const formData = new FormData(e.target.name);

        try {
            const response = await api.patch(`/usuarios/editar/${usuario._id}`, formData, {
            headers: {
                Authorization: `Bearer ${JSON.parse(token)}`
            }
            });
            setFlashMessage(response.data.message, msgType);
        } catch (error) {
            setFlashMessage(error.response.data.message || error.message, 'error');
        }

    }

  return (
    <section>
            <h1>Editar</h1>
            <form onSubmit={handleSubmit}>
                <Input
                text="Nome: "
                type="text"
                name="nome"
                placeholder="Digite o seu nome"
                handleOnChange={handleChange}
                value={usuario.nome || ""}
                />
                <Input
                text="E-mail: "
                type="text"
                name="email"
                placeholder="Digite o seu e-mail"
                handleOnChange={handleChange}
                value={usuario.email || ""}
                />
                <Input
                text="Telefone: "
                type="text"
                name="telefone"
                placeholder="Digite o seu telefone"
                handleOnChange={handleChange}
                value={usuario.telefone || ""}
                />
                <Input
                text="Senha: "
                type="password"
                name="senha"
                placeholder="Digite a sua senha"
                handleOnChange={handleChange}
                value={usuario.senha || ""}
                />
                <button type="submit">Registrar</button>
            </form>
        </section>
  )
}

export default Profile
