import React from 'react'
import { useState } from 'react'
import { useNavigate } from 'react-router-dom'

// Componentes
import Input from '../../form/Input'

// utils
import api from '../../../utils/api'

// Hooks
import useFlashMessage from '../../../hooks/useFlashMessage'


function CadastrarProdutos() {
  const [produto, setProduto] = useState({})
  const [preview, setPreview] = useState([])
  const [token] = useState(localStorage.getItem('token'))
  const navigate = useNavigate()

  // Usar as mensagens
  const {setFlashMessage} = useFlashMessage()

  // Pegar os valores dos inputs
  function handleFIleChange(e){
    setPreview(e.target.files[0])
    setProduto({...produto, [e.target.name]: e.target.files[0]})
  }

  // Pegar os valores dos inputs
  function handleChange(e) {
    setProduto({...produto, [e.target.name]: e.target.value})
  }

  // Função para cadastrar o produto
  async function handleSubmit(e){
    e.preventDefault()
    let msgType = 'sucess'

    try {
      const formData = new FormData();
      formData.append('imagens', produto.imagens);
      formData.append('nome', produto.nome);
      formData.append('descricao', produto.descricao);
      formData.append('valor', produto.valor);

      const response = await api.post(`/produtos/cadastrar`, formData, {
        headers: {
          Authorization: `Bearer ${JSON.parse(token)}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      setFlashMessage(response.data.message, msgType);
    } catch (error) {
      setFlashMessage(error.response.data.message || error.message, 'error');
    }

  }

  return (
              <form onSubmit={handleSubmit}>
                <div>

                    {(produto.imagens || preview) && (
                      <img src={preview ? URL.createObjectURL(preview) : `${process.env.REACT_APP_API}/images/produtos/${imagens}`} alt={usuario.nome}/>
                    )}

                </div>
                <Input
                text="Imagem: "
                type="file"
                name="imagens"
                handleOnChange={handleFIleChange}
                multiple={true}
                />
                <Input
                text="Produto: "
                type="text"
                name="nome"
                placeholder="Digite o nome do produto"
                handleOnChange={handleChange}
                />
                <Input
                text="Descrição: "
                type="text"
                name="descricao"
                placeholder="Digite a descrição do produto"
                handleOnChange={handleChange}
                />
                <Input
                text="Valor: "
                type="number"
                name="valor"
                placeholder="Digite o valor do produto"
                handleOnChange={handleChange}
                />
                <button type="submit">Cadastrar produto</button>
              </form>

  )
}

export default CadastrarProdutos
